import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMovieDetails } from '../api/tmdb'; // Ganti sesuai dengan API yang digunakan

const MovieDetail = () => {
  const { movieId } = useParams(); // Mengambil movieId dari URL
  const [movie, setMovie] = useState(null);
  const [trailerKey, setTrailerKey] = useState(null);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      const data = await getMovieDetails(movieId); // Ambil data film berdasarkan ID
      setMovie(data);

      // Cari trailer resmi dari video yang tersedia
      const trailer = data.videos.results.find(
        (video) => video.site === 'YouTube' && video.type === 'Trailer'
      );
      if (trailer) {
        setTrailerKey(trailer.key);
      }
    };
    fetchMovieDetails();
  }, [movieId]);

  if (!movie) return <div className="text-white text-center">Loading...</div>;

  return (
    <div
      className="relative h-screen w-full overflow-hidden"
      style={{
        backgroundImage: `url('https://image.tmdb.org/t/p/original${movie.backdrop_path}')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      {/* Overlay Gelap */}
      <div className="absolute inset-0 bg-black opacity-50"></div>

      <div className="relative z-10 flex flex-col justify-center h-full p-8 md:p-16">
        <div className="max-w-4xl mx-auto bg-black bg-opacity-70 rounded-2xl p-8 shadow-lg">
          <h1 className="text-4xl md:text-6xl font-extrabold text-white mb-4 uppercase tracking-wider">
            {movie.title}
          </h1>
          <p className="text-lg text-gray-300 leading-relaxed mb-8">
            {movie.overview}
          </p>

          {/* Kontainer Responsif untuk Iframe */}
          {trailerKey && (
            <div className="aspect-w-16 aspect-h-9 rounded-xl overflow-hidden">
              <iframe
                className="w-full h-full"
                src={`https://www.youtube.com/embed/${trailerKey}?autoplay=1&mute=1`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="Movie Trailer"
              ></iframe>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MovieDetail;
